mapper.insert:
INSERT
  INTO 
	  BOARD_REPLY
VALUES
	  (
	  SEQ_BOARD_REPLY.NEXTVAL
	 ,#{ boardNo }
	 ,#{ memberId }
	 ,#{ boardReplyContent } 
	 ,NULL
	 ,DEFAULT
	 ,DEFAULT
	 )

INSERT
  INTO
  	  BOARD_RE_REPLY
VALUES
	  (
	  SEQ_BOARD_RE_REPLY.NEXTVAL
	 ,#{ replyNo }
	 ,#{ memberId }
	 ,#{ reReplyContent }
	 ,SYSDATE
	 ,NULL 
	 ,DEFAULT
	 )

mapper.select:
SELECT COUNT(*) FROM MAINBOARD LEFT JOIN HASHTAG_BRIDGE USING(BOARD_NO) WHERE BOARD_STATUS = 'Y' AND BOARD_TYPE = #{ boardType }
AND HASHTAG_NAME LIKE '%' || #{ key } || '%'
AND ( BOARD_CONTENT LIKE '%' || #{ key } || '%' OR BOARD_TITLE LIKE '%' || #{ key } || '%' )

SELECT A.BOARD_NO ,NICKNAME MEMBER_ID ,CATEGORY ,BOARD_TITLE ,BOARD_CONTENT ,TO_CHAR(CREATE_DATE, 'YYYY-MM-DD HH24:MI') AS "CREATE_DATE" ,BOARD_COUNT ,BOARD_TYPE ,SUMMARY ,COUNT(STATUS_LB)OVER(PARTITION BY B.BOARD_NO) LIKECOUNT ,(COUNT(BOARD_REPLY_CONTENT)OVER(PARTITION BY C.BOARD_NO)) + (NVL(REREPLY,0)) COMMENTCOUNT FROM MAINBOARD A JOIN MEMBER USING(MEMBER_ID) LEFT JOIN (SELECT BOARD_NO,STATUS_LB FROM BOARD_LB WHERE STATUS_LB = 'L') B ON (A.BOARD_NO = B.BOARD_NO) LEFT JOIN BOARD_REPLY C ON(C.BOARD_NO = A.BOARD_NO) LEFT JOIN (SELECT COUNT(BOARD_NO) REREPLY, BOARD_NO FROM BOARD_RE_REPLY JOIN BOARD_REPLY USING(BOARD_REPLY_NO) GROUP BY BOARD_NO) D ON(D.BOARD_NO = A.BOARD_NO) LEFT JOIN HASHTAG_BRIDGE F ON(F.BOARD_NO = A.BOARD_NO) WHERE A.BOARD_STATUS = 'Y'
AND HASHTAG_NAME LIKE '%' || #{ key } || '%'
AND ( BOARD_CONTENT LIKE '%' || #{ key } || '%' OR BOARD_TITLE LIKE '%' || #{ key } || '%' )
AND BOARD_TYPE = #{ boardType }
AND CATEGORY = #{ category }
ORDER BY CREATE_DATE DESC

SELECT COUNT(*) FROM MAINBOARD A JOIN MEMBER USING(MEMBER_ID) LEFT JOIN (SELECT BOARD_NO,STATUS_LB FROM BOARD_LB WHERE STATUS_LB = 'L') B ON (A.BOARD_NO = B.BOARD_NO) LEFT JOIN BOARD_REPLY C ON(C.BOARD_NO = A.BOARD_NO) LEFT JOIN (SELECT COUNT(BOARD_NO) REREPLY, BOARD_NO FROM BOARD_RE_REPLY JOIN BOARD_REPLY USING(BOARD_REPLY_NO) GROUP BY BOARD_NO) D ON(D.BOARD_NO = A.BOARD_NO) LEFT JOIN HASHTAG_BRIDGE F ON(F.BOARD_NO = A.BOARD_NO) WHERE A.BOARD_STATUS = 'Y' AND BOARD_TYPE = #{ boardType }
AND HASHTAG_NAME LIKE '%' || #{ key } || '%'
AND (BOARD_CONTENT LIKE '%' || #{ key } || '%' OR BOARD_TITLE LIKE '%' || #{ key } || '%')
AND CATEGORY = #{ category }

mapper.update:
UPDATE
	  MAINBOARD
   SET
      BOARD_TITLE = #{ boardTitle }
     ,BOARD_CONTENT = #{ boardContent }
     ,CATEGORY = #{ category }
 WHERE
      BOARD_NO = #{ boardNo }

UPDATE
	  HASHTAG_BRIDGE
   SET
   	  HASHTAG_NAME = #{ tagList }
 WHERE
 	  BOARD_NO = #{ boardNo }

UPDATE
	  BOARD_REPLY
   SET
      BOARD_REPLY_CONTENT = #{ boardReplyContent }
 WHERE
 	  BOARD_REPLY_NO = #{ boardReplyNo }

UPDATE
	  BOARD_RE_REPLY
   SET
      BOARD_REPLY2_CONTENT = #{ reReplyContent }
 WHERE
 	  BOARD_RE_REPLY_NO = #{ reReplyNo }

