  	  )

mapper.select:
SELECT * FROM (SELECT SB_NO ,NICKNAME AS "MEMBER_ID" ,SB_TITLE ,SB_INTRODUCE ,SB_CHANGE_NAME ,SB_RECRUIT_MEM ,SB_NOW_MEM ,CATEGORY FROM SBAND JOIN MEMBER USING(MEMBER_ID) WHERE SB_STATUS = 'Y'
AND CATEGORY = #{ category }
ORDER BY CREATE_DATE DESC) WHERE ROWNUM < 6
