mapper.update:
UPDATE
	CALENDAR
SET
	CALENDAR_TITLE=#{calendarTitle}
	,CALENDAR_CONTENT=#{calendarContent}
	,START_DAY=#{startDay}
	,END_DAY=#{endDay}
WHERE
	CALENDAR_NO=#{calendarNo}
AND
	MEMBER_ID=#{memberId}
AND
	TYPE='C'

UPDATE
	MEMBER
SET	
	MEMBER_PWD = #{memberPwd}
	,MEMBER_NAME = #{memberName}
	,MEMBER_EMAIL = #{memberEmail}
	,NICKNAME = #{nickName}
WHERE
	MEMBER_ID = #{memberId}

UPDATE
		MEMBER
	SET
		MEMBER_PWD = #{memberPwd}
	WHERE
		MEMBER_EMAIL = #{memberEmail}

mapper.select:
SELECT COUNT(*)
  FROM POINT
 WHERE MEMBER_ID = #{ memberId }

SELECT COUNT(*) FROM MAINBOARD WHERE MEMBER_ID = #{memberId}
AND BOARD_TYPE = 'free'
AND BOARD_TYPE = 'info'

mapper.delete:
DELETE
  FROM QNA
 WHERE QNA_NO = ${ qno }
